import axios from "axios";

const API = axios.create({ baseURL: "http://localhost:5000/api" }); // your backend URL

export const fetchPosts = () => API.get("/news");
export const createPost = (newPost) => API.post("/news", newPost);
export const filterByCategory = (category) => API.get(`/news/category/${category}`);
