export default function NewsCard({ post, onClick }) {
  const cardStyle = {
    background: 'white',
    borderRadius: '12px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    width: '400px',
    cursor: 'pointer',
    transition: 'transform 0.2s ease-in-out',
    overflow: 'hidden',
    border: '1px solid #e0e0e0',
  };

  const imageStyle = {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
  };

  const contentStyle = {
    padding: '1rem',
  };

  const titleStyle = {
    fontSize: '1.2rem',
    marginTop: '0.5rem',
    marginBottom: '0.5rem',
    color: '#333',
  };

  const summaryStyle = {
    fontSize: '0.9rem',
    color: '#666',
    lineHeight: '1.4',
  };

  const actionsStyle = {
    display: 'flex',
    justifyContent: 'flex-end',
    gap: '15px',
    marginTop: '1rem',
  };

  const iconStyle = {
    fontSize: '1.2rem',
    color: '#8B0000',
    cursor: 'pointer',
  };

  return (
    <div style={cardStyle} onClick={onClick}>
      {post.image && <img src={post.image} alt={post.title} style={imageStyle} />}
      <div style={contentStyle}>
        <h2 style={titleStyle}>{post.title}</h2>
        <p style={summaryStyle}>{post.summary}</p>
        <div style={actionsStyle}>
          <span style={iconStyle}>üëç</span>
          <span style={iconStyle}>üîñ</span>
          <span style={iconStyle}>üîó</span>
          <span style={iconStyle}>üìä</span>
        </div>
      </div>
    </div>
  );
}
