import React, { useState, useEffect } from 'react';
import CreatePostForm from "../components/CreatePostForm";
import { API } from '../services/api';
import NewsCard from '../components/NewsCard';

export default function CreatePost() {
  const [myPosts, setMyPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [imagePreview, setImagePreview] = useState(null); // State for image preview

  useEffect(() => {
    const fetchMyPosts = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await API.get('/posts/myposts', {
          headers: {
            'x-auth-token': token,
          },
        });
        setMyPosts(res.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchMyPosts();
  }, []);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setImagePreview(null);
    }
  };

  return (
    <div className="create-post-page">
      <div className="create-post-container">
        {/* Left Column: Create Post Form */}
        <div className="create-post-left">
          <div className="image-preview-section">
            {imagePreview ? (
              <img src={imagePreview} alt="Image Preview" className="image-preview" />
            ) : (
              <div className="image-placeholder">Image Preview</div>
            )}
          </div>
          <CreatePostForm />
        </div>

        {/* Right Column: Image Upload/Generate and My Posts */}
        <div className="create-post-right">
          <div className="image-upload-section">
            <label htmlFor="upload-image" className="upload-button">
              upload poster image
            </label>
            <input
              id="upload-image"
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              style={{ display: 'none' }}
            />
            <div className="folder-icon"></div> {/* Placeholder for folder icon */}
          </div>
          <div className="divider">
            <span>or generate poster image</span>
            <span className="not-now-future">not now in future</span>
          </div>
          <div className="generate-image-section">
            <input type="text" placeholder="add prompt" className="prompt-input" />
            <button className="generate-button">generate</button>
          </div>

          <div className="my-posts-section">
            <h2>My Posts</h2>
            {loading ? (
              <div>Loading...</div>
            ) : (
              <div className="news-grid">
                {myPosts.length > 0 ? (
                  myPosts.map((post) => <NewsCard key={post._id} post={post} />)
                ) : (
                  <p>You have not created any posts yet.</p>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
