import { useState } from "react";
import { createPost } from "../services/api";

export default function CreatePostForm() {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    source: "",
    category: "",
    image: "",
  });

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await createPost(formData);
    alert("Post Created Successfully!");
  };

  const containerStyle = {
    display: 'flex',
    gap: '30px',
    background: 'white',
    padding: '30px',
    borderRadius: '12px',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
    maxWidth: '1200px',
    width: '100%',
  };

  const uploadSectionStyle = {
    flex: 1,
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  };

  const uploadBoxStyle = {
    border: '2px dashed #ccc',
    borderRadius: '8px',
    padding: '40px 20px',
    textAlign: 'center',
    color: '#888',
    fontSize: '1.1rem',
    backgroundColor: '#fdfdfd',
  };

  const inputStyle = {
    width: '100%',
    padding: '12px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    fontSize: '1rem',
    boxSizing: 'border-box',
  };

  const formActionsStyle = {
    display: 'flex',
    gap: '15px',
    marginTop: '10px',
  };

  const buttonStyle = {
    padding: '12px 25px',
    border: 'none',
    borderRadius: '8px',
    fontSize: '1rem',
    cursor: 'pointer',
  };

  const previewSectionStyle = {
    flex: 1,
    display: 'flex',
    flexDirection: 'column',
    gap: '20px',
    alignItems: 'center',
  };

  const postPreviewStyle = {
    width: '100%',
    maxWidth: '400px',
    border: '1px solid #eee',
    borderRadius: '12px',
    overflow: 'hidden',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
    position: 'relative',
  };

  const previewImageStyle = {
    width: '100%',
    height: '250px',
    objectFit: 'cover',
  };

  const previewOverlayStyle = {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    background: 'rgba(0, 0, 0, 0.6)',
    color: 'white',
    padding: '15px',
  };

  const designToolsStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
    marginTop: '20px',
  };

  const toolItemStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    gap: '5px',
    color: '#555',
    fontSize: '0.9rem',
    cursor: 'pointer',
  };

  return (
    <div style={containerStyle}>
      <div style={uploadSectionStyle}>
        <div style={uploadBoxStyle}>
          <p>upload poster/ drag & drop</p>
        </div>
        <input name="title" placeholder="Add title" onChange={handleChange} required style={inputStyle} />
        <textarea name="description" placeholder="Add description" onChange={handleChange} required style={inputStyle} />
        <input name="source" placeholder="Add source/credits" onChange={handleChange} style={inputStyle} />
        <select name="category" onChange={handleChange} required style={inputStyle}>
          <option value="">choose categorie</option>
          <option value="Top">Top</option>
          <option value="Global">Global</option>
          <option value="India">India</option>
          <option value="Market">Market</option>
          <option value="Karnataka">Karnataka</option>
        </select>
        <div style={formActionsStyle}>
          <button type="button" style={{...buttonStyle, backgroundColor: '#e0e0e0', color: '#333'}}>save as draft</button>
          <button type="submit" style={{...buttonStyle, backgroundColor: '#8B0000', color: 'white'}}>post</button>
        </div>
      </div>
      <div style={previewSectionStyle}>
        <p>or, Edit your own post</p>
        <div style={postPreviewStyle}>
          <img src={formData.image || "https://via.placeholder.com/300x200?text=Post+Preview"} alt="Post Preview" style={previewImageStyle} />
          <div style={previewOverlayStyle}>
            <h3>{formData.title || "Electoral Bonds Verdict: SC Strikes Down Scheme"}</h3>
            <p>{formData.description || "Orders Transparency & Accountability"}</p>
          </div>
        </div>
        <div style={designToolsStyle}>
          <div style={toolItemStyle}>
            <span>‚¨ÜÔ∏è</span>
            <span>Upload</span>
          </div>
          <div style={toolItemStyle}>
            <span>üñºÔ∏è</span>
            <span>Elements</span>
          </div>
          <div style={toolItemStyle}>
            <span>T</span>
            <span>Text</span>
          </div>
          <div style={toolItemStyle}>
            <span>üé®</span>
            <span>colors</span>
          </div>
        </div>
      </div>
    </div>
  );
}
