import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from "react-router-dom";
import logo from "../assets/logo.png";
import "../App.css";
import Modal from './Modal';
import LoginForm from './LoginForm';

export function Header() {
  return (
    <div className="header">
      <div className="header-brand" >
        <img src={logo} alt="Infoposter Logo" className="header-logo" />
        <span className="header-title">Infoposter</span>
      
      </div>
      <div className="header-actions">
        <div className="user-avatar">L</div>
      </div>
    </div>
  );
}

export function Navbar() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsAuthenticated(!!token);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    navigate('/'); // Redirect to home after logout
  };

  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    setShowLoginModal(false);
    navigate('/'); // Redirect to home after login
  };

  const openLoginModal = () => {
    setShowLoginModal(true);
  };

  const closeLoginModal = () => {
    setShowLoginModal(false);
  };

  return (
    <nav className="navbar">
      <div className="navbar-links">
        <Link to="/" className="nav-link-button">
          Home
        </Link>
        <Link to="/saved" className="nav-link-button">
          Saved
        </Link>
      </div>
      <div className="navbar-actions">
        <Link to="/create" className="creator-button">
          Creator
        </Link>
        {isAuthenticated ? (
          <button onClick={handleLogout} className="nav-link-button">
            Logout
          </button>
        ) : (
          <>
            <button onClick={openLoginModal} className="nav-link-button">
              Login
            </button>
          </>
        )}
      </div>

      <Modal show={showLoginModal} onClose={closeLoginModal} title="wellcome to infoposter">
        <LoginForm onLoginSuccess={handleLoginSuccess} />
      </Modal>
    </nav>
  );
}
