import React, { useState } from 'react';
import api from '../services/api';

export default function NewsCard({ post, onClick }) {
  const [isSaved, setIsSaved] = useState(false);

  const handleSave = async (e) => {
    e.stopPropagation(); // Prevent card click event
    const token = localStorage.getItem('token');
    if (!token) {
      // Handle not logged in case
      return;
    }

    try {
      if (isSaved) {
        await api.delete(`/posts/${post._id}/save`, {
          headers: { 'x-auth-token': token },
        });
        setIsSaved(false);
      } else {
        await api.put(`/posts/${post._id}/save`, {}, {
          headers: { 'x-auth-token': token },
        });
        setIsSaved(true);
      }
    } catch (err) {
      console.error(err);
    }
  };

  const cardStyle = {
    lineHeight: '1.4',
  };

  const actionsStyle = {
    display: 'flex',
    justifyContent: 'flex-end',
    gap: '15px',
    marginTop: '1rem',
  };

  const iconStyle = {
    fontSize: '1.2rem',
    color: '#8B0000',
    cursor: 'pointer',
  };

  return (
    <div style={cardStyle} onClick={onClick}>
      {post.image && <img src={post.image} alt={post.title} style={imageStyle} />}
      <div style={contentStyle}>
        <h2 style={titleStyle}>{post.title}</h2>
        <p style={summaryStyle}>{post.summary}</p>
        <div style={actionsStyle}>
          <span style={iconStyle}>üëç</span>
          <span style={iconStyle}>üîñ</span>
          <span style={iconStyle}>üîó</span>
          <span style={iconStyle}>üìä</span>
        </div>
      </div>
    </div>
  );
}
