import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from "react-router-dom";
import logo from "../assets/logo.png";
import "../App.css";

export function Header() {
  return (
    <div className="header">
      <div className="header-brand" >
        <img src={logo} alt="Infoposter Logo" className="header-logo" />
        <span className="header-title">Infoposter</span>
      
      </div>
      <div className="header-actions">
        <div className="user-avatar">L</div>
      </div>
    </div>
  );
}

export function Navbar() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsAuthenticated(!!token);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="navbar-links">
        <Link to="/" className="nav-link-button">
          Home
        </Link>
        <Link to="/saved" className="nav-link-button">
          Saved
        </Link>
      </div>
      <div className="navbar-actions">
        <Link to="/create" className="creator-button">
          Creator
        </Link>
        {isAuthenticated ? (
          <button onClick={handleLogout} className="nav-link-button">
            Logout
          </button>
        ) : (
          <>
            <button className="nav-link-button">
              Login
            </button>
            <button className="nav-link-button">
              Register
            </button>
          </>
        )}
      </div>
    </nav>
  );
}
