import { Link } from "react-router-dom";
import logo from "../assets/logo.png";

export default function Navbar() {
  const navStyle = {
    background: '#8B0000',
    color: 'white',
    padding: '10px 20px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
  };

  const brandStyle = {
    display: 'flex',
    alignItems: 'center',
  };

  const logoStyle = {
    height: '40px',
    marginRight: '10px',
  };

  const titleStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
  };

  const linkContainerStyle = {
    display: 'flex',
    gap: '20px',
  };

  const linkStyle = {
    color: 'black',
    backgroundColor: 'white',
    textDecoration: 'none',
    fontSize: '18px',
    padding: '8px 15px',
    borderRadius: '20px',
    transition: 'background-color 0.3s ease',
  };

  const actionsStyle = {
    display: 'flex',
    alignItems: 'center',
    gap: '15px',
  };

  const creatorButtonStyle = {
    backgroundColor: 'transparent',
    color: 'white',
    padding: '8px 15px',
    borderRadius: '5px',
    textDecoration: 'none',
    fontWeight: 'bold',
    border: '1px solid white',
  };

  const avatarStyle = {
    backgroundColor: '#D3D3D3',
    color: '#8B0000',
    width: '40px',
    height: '40px',
    borderRadius: '50%',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontWeight: 'bold',
    fontSize: '20px',
  };

  return (
    <nav style={navStyle}>
      <div style={brandStyle}>
        <img src={logo} alt="Infoposter Logo" style={logoStyle} />
        <span style={titleStyle}>Infoposter</span>
      </div>
      <div style={linkContainerStyle}>
        <Link to="/" style={linkStyle}>Home</Link>
        <Link to="/saved" style={linkStyle}>Saved</Link>
      </div>
      <div style={actionsStyle}>
        <Link to="/create" style={creatorButtonStyle}>Creator</Link>
        <div style={avatarStyle}>L</div>
      </div>
    </nav>
  );
}
